import React from 'react'
import { Formik } from 'formik';
import './login.scss'
import {BASEURL,URLENDPOINT} from "../../constants"
import axios from '../../config/api';
import Cookies from 'universal-cookie';

const index = () => {
  const cookies = new Cookies();


  const login =async(reqBody)=>{
    const response=axios("POST",BASEURL.url + URLENDPOINT.login,{},{},reqBody)
    // const response=axios.post(`${BASEURL.url+URLENDPOINT.login}`,reqBody)
    .then((res)=>{
      console.log("res",res);
      cookies.set('loginData', res?.data, { path: '/' });
    }).catch((err)=>{console.log("err",err);})

  }
  return (
    <div id='login'>
        <div className='mainConatiner'>
            <div className='loginSub'>
               <div>
     <h1 className='centerText'>LOGIN</h1>
     <Formik
       initialValues={{ email: '', password: '' }}
       validate={values => {
         const errors = {};
         if (!values.email) {
           errors.email = 'Required';
         } else if (
           !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
         ) {
           errors.email = 'Invalid email address';
         }
         return errors;
       }}
       onSubmit={(values, { setSubmitting }) => {
        console.log(values);
        login(values)
        //  setTimeout(() => {
        //    alert(JSON.stringify(values, null, 2));
        //    setSubmitting(false);
        //  }, 400);
       }}
     >
       {({
         values,
         errors,
         touched,
         handleChange,
         handleBlur,
         handleSubmit,
         isSubmitting,
         /* and other goodies */
       }) => (
         <form  className='alignInput' onSubmit={handleSubmit}>
           <input
            className='inputField'
             type="email"
             name="email"
             onChange={handleChange}
             onBlur={handleBlur}
             value={values.email}
             placeholder='Email ID'
           />
           {errors.email && touched.email && errors.email}
           <input
           className='inputField'
             type="password"
             name="password"
             onChange={handleChange}
             onBlur={handleBlur}
             value={values.password}
             placeholder='Password'
           />
           {errors.password && touched.password && errors.password}
           <button className='inputField submitButton' type="submit" disabled={isSubmitting}>
             Submit
           </button>
         </form>
       )}
     </Formik>
   </div>
            </div>
        </div>
    </div>
  )
}

export default index
