import React,{useState} from 'react'
import './dashboard.scss'

const Dashboard = () => {
    // const [selectedData,setSelectedData]=useState([])
    const selected=[]
const data = [
    { name: "Anom", age: 19, gender: "Male",doj:"11/10/22"},
    { name: "Megha", age: 19, gender: "Female",doj:"11/10/22" },
    { name: "Subham", age: 25, gender: "Male" ,doj:"11/10/22"},
]

const selectedValues=(e,val)=>{
    console.log(e.target.checked);
    if(e.target.checked){
    selected.push(val)
    }
    else{
        var index = selected.indexOf(val);
            if (index > -1) {
                selected.splice(index, 1);
            }
    }
    console.log(selected);
}

  return (
     <div id='dashboard'>
        <div className='buttonSet'>
            <button>Upload</button>
            <button>Download</button>
        </div>
            <table>
                <tr>
                    <th></th>
                    <th>Plan Type</th>
                    <th>Company Name</th>
                    <th>Emp Mobile No</th>
                    <th>Work Email</th>
                    <th>Grade Card</th>
                    <th>Emp Type</th>
                    <th>Emp Name</th>
                    <th>Designation</th>
                    <th>Work Loaction</th>
                    <th>DOJ</th>
                </tr>
                {data.map((val, key) => {
                    return (<>

                        <tr key={key}>
                            <input type="checkbox" onChange={(e)=>{selectedValues(e,val)}}/>
                            <td>{val.name}</td>
                            <td>{val.age}</td>
                            <td>{val.gender}</td>
                            <td>{val.doj}</td>
                            <td>{val.doj}</td>
                            <td>{val.doj}</td>
                            <td>{val.doj}</td>
                            <td>{val.doj}</td>
                            <td>{val.doj}</td>
                            <td>{val.doj}</td>
                        </tr>
                        </>
                    )
                })}
            </table>
        </div>
  )
}

export default Dashboard
