import React,{useState} from 'react'
import CsvDownloader from "react-csv-downloader";
import './dashboard.scss'

const Dashboard = () => {
    // const [selectedData,setSelectedData]=useState([])
    const selected=[]
const data = [
    { plan_type: "Anom", age: 19, gender: "Male",doj:"11/10/22"},
    { plan_type: "Megha", age: 19, gender: "Female",doj:"11/10/22" },
    { plan_type: "Subham", age: 25, gender: "Male" ,doj:"11/10/22"},
]

const selectedValues=(e,val)=>{
    console.log(e.target.checked);
    if(e.target.checked){
    selected.push(val)
    }
    else{
        var index = selected.indexOf(val);
            if (index > -1) {
                selected.splice(index, 1);
            }
    }
    console.log(selected);
}

 const columns = [{
    id: 'plan_type',
    displayName: 'Plan Type'
  }, {
    id: 'Company_Name',
    displayName: 'Company Name'
  }];

  return (
     <div id='dashboard'>
        <div className='buttonSet'>
            <button className='uplaodButton'>Upload</button>
              <CsvDownloader
              filename={"Task List"}
              extension=".csv"
              enclosingCharacter={``}
              separator=","
              columns={columns}
              datas={data}
            >            <button className='downloadButton'>Export</button>
</CsvDownloader>
        </div>
            <table>
                <tr className='head'>
                    <th></th>
                    <th>Plan Type</th>
                    <th>Company Name</th>
                    <th>Emp Mobile No</th>
                    <th>Work Email</th>
                    <th>Grade Card</th>
                    <th>Emp Type</th>
                    <th>Emp Name</th>
                    <th>Designation</th>
                    <th>Work Loaction</th>
                    <th>DOJ</th>
                </tr>
                {data.map((val, key) => {
                    return (<>

                        <tr key={key}>
                            <input type="checkbox" onChange={(e)=>{selectedValues(e,val)}}/>
                            <td>{val.plan_type}</td>
                            <td>{val.age}</td>
                            <td>{val.gender}</td>
                            <td>{val.doj}</td>
                            <td>{val.doj}</td>
                            <td>{val.doj}</td>
                            <td>{val.doj}</td>
                            <td>{val.doj}</td>
                            <td>{val.doj}</td>
                            <td>{val.doj}</td>
                        </tr>
                        </>
                    )
                })}
            </table>
        </div>
  )
}

export default Dashboard
